import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import CreateSaleForm from './components/CreateSaleForm';
import TicketList from './components/TicketList';
import './App.css';
import { contractAddress, contractABI } from './contractConfig'; // Pastikan Anda mengimpor ABI dan alamat kontrak

const App = () => {
  const [account, setAccount] = useState('');
  const [contract, setContract] = useState(null);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);
        console.log("Connected account:", accounts[0]);

        // Menghubungkan ke kontrak pintar
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const ticketContract = new ethers.Contract(contractAddress, contractABI, signer);
        setContract(ticketContract);

      } catch (error) {
        console.error("User denied wallet connection:", error);
      }
    } else {
      alert("Please install Metamask");
    }
  };

  useEffect(() => {
    connectWallet();
  }, []);

  return (
    <div>
      <h1>Concert Ticket Sale</h1>
      {account ? (
        <p>Connected as: {account}</p>
      ) : (
        <p>Please connect your wallet.</p>
      )}
      {contract && (
        <>
          <CreateSaleForm contract={contract} userAddress={account} />
          <TicketList contract={contract} />
        </>
      )}
    </div>
  );
};

export default App;
