import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';

function TicketList({ contract }) {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTickets = async () => {
      setLoading(true);
      try {
        if (contract) {
          const ticketCount = await contract.getTicketCount();
          const ticketList = [];

          for (let i = 1; i <= ticketCount; i++) {
            const ticket = await contract.getTicket(i);
            ticketList.push({
              id: i,
              eventName: ticket[0],
              eventOrganizer: ticket[1],
              posterUrl: ticket[2], // Assuming ticket[2] is the poster URL
              price: ticket[3],
              expiryTimestamp: ticket[4],
              sold: ticket[5],
              downloaded: ticket[6],
            });
          }

          setTickets(ticketList);
          console.log('Fetched tickets:', ticketList);
        }
      } catch (error) {
        console.error('Error fetching tickets:', error);
        alert('Failed to fetch tickets. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchTickets();
  }, [contract]);

  const handleBuyTicket = async (ticketId, price) => {
    try {
      const tx = await contract.buyTicket(ticketId, { value: price });
      await tx.wait();
      alert(`Ticket ${ticketId} successfully purchased.`);
    } catch (error) {
      console.error('Error buying ticket:', error);
      alert('Failed to buy ticket.');
    }
  };

  const handleDownload = async (ticketId) => {
    try {
      const tx = await contract.markAsDownloaded(ticketId);
      await tx.wait();
      alert(`Ticket ${ticketId} has been downloaded.`);
    } catch (error) {
      console.error('Error downloading ticket:', error);
      alert('Failed to download ticket.');
    }
  };

  return (
    <div>
      <h2>Daftar Tiket</h2>
      {loading ? (
        <p>Loading tickets...</p>
      ) : tickets.length === 0 ? (
        <p>Belum ada tiket, silahkan buat penjualan.</p>
      ) : (
        tickets.map((ticket) => (
          <div key={ticket.id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
            <h3>{ticket.eventName}</h3>
            <img 
              src={`https://gateway.pinata.cloud/ipfs/${ticket.posterUrl}`} 
              alt={ticket.eventName} 
              style={{ width: '150px', height: 'auto', marginBottom: '10px' }} 
            />
            <p>Organizer: {ticket.eventOrganizer}</p>
            <p>Price: {ethers.utils.formatEther(ticket.price)} ETH</p>
            <p>Sale End: {new Date(ticket.expiryTimestamp * 1000).toLocaleString()}</p>

            {ticket.sold ? (
              <button onClick={() => handleDownload(ticket.id)} disabled={ticket.downloaded}>
                {ticket.downloaded ? 'Ticket Already Downloaded' : 'Download Ticket'}
              </button>
            ) : (
              <button onClick={() => handleBuyTicket(ticket.id, ticket.price)}>
                Beli Tiket
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
}

export default TicketList;
