import React, { useState } from 'react';
import { uploadImage } from '../utils/api';

const CreateSaleForm = () => {
    const [image, setImage] = useState(null);
    const [eventName, setEventName] = useState('');
    const [eventOrganizer, setEventOrganizer] = useState('');
    const [posterUrl, setPosterUrl] = useState('');
    const [ticketImageUrl, setTicketImageUrl] = useState('');
    const [price, setPrice] = useState('');
    const [expiryTimestamp, setExpiryTimestamp] = useState('');
    const [eventDate, setEventDate] = useState('');
    const [ipfsHash, setIpfsHash] = useState('');

    const handleFileChange = (event) => {
        setImage(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!image) {
            alert('Please select an image');
            return;
        }

        try {
            const result = await uploadImage(image);
            setIpfsHash(result.IpfsHash);
            alert('Image uploaded successfully. IPFS Hash: ' + result.IpfsHash);

            // Anda bisa memanggil fungsi untuk membuat tiket di sini dengan semua parameter
            // Misalnya: createTicket(eventName, eventOrganizer, posterUrl, ticketImageUrl, price, expiryTimestamp, eventDate);
        } catch (error) {
            console.error('Failed to upload image:', error);
        }
    };

    return (
        <div>
            <h2>Create a New Ticket</h2>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    placeholder="Event Name" 
                    value={eventName} 
                    onChange={(e) => setEventName(e.target.value)} 
                    required 
                />
                <input 
                    type="text" 
                    placeholder="Event Organizer" 
                    value={eventOrganizer} 
                    onChange={(e) => setEventOrganizer(e.target.value)} 
                    required 
                />
                <input 
                    type="text" 
                    placeholder="Poster URL" 
                    value={posterUrl} 
                    onChange={(e) => setPosterUrl(e.target.value)} 
                    required 
                />
                <input 
                    type="text" 
                    placeholder="Ticket Image URL" 
                    value={ticketImageUrl} 
                    onChange={(e) => setTicketImageUrl(e.target.value)} 
                    required 
                />
                <input 
                    type="number" 
                    placeholder="Price" 
                    value={price} 
                    onChange={(e) => setPrice(e.target.value)} 
                    required 
                />
                <input 
                    type="number" 
                    placeholder="Expiry Timestamp" 
                    value={expiryTimestamp} 
                    onChange={(e) => setExpiryTimestamp(e.target.value)} 
                    required 
                />
                <input 
                    type="number" 
                    placeholder="Event Date" 
                    value={eventDate} 
                    onChange={(e) => setEventDate(e.target.value)} 
                    required 
                />
                <input type="file" onChange={handleFileChange} required />
                <button type="submit">Create Ticket</button>
            </form>
            {ipfsHash && <p>IPFS Hash: {ipfsHash}</p>}
        </div>
    );
};

export default CreateSaleForm;
