import { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import axios from 'axios';

function CreateSaleForm({ contract, userAddress }) {
  const [eventName, setEventName] = useState('');
  const [eventOrganizer, setEventOrganizer] = useState('');
  const [eventDate, setEventDate] = useState(''); // Hanya tanggal
  const [ticketPrice, setTicketPrice] = useState('');
  const [saleEndDate, setSaleEndDate] = useState(''); // Hanya tanggal
  const [posterFile, setPosterFile] = useState(null);
  const [ticketImageFile, setTicketImageFile] = useState(null);
  const [posterPreview, setPosterPreview] = useState('');
  const [ticketImagePreview, setTicketImagePreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [ticketCount, setTicketCount] = useState(0);

  useEffect(() => {
    const fetchTicketCount = async () => {
      if (contract) {
        try {
          const count = await contract.getTicketCount();
          setTicketCount(count.toNumber());
        } catch (error) {
          console.error('Error memuat jumlah tiket:', error);
        }
      }
    };

    fetchTicketCount();
  }, [contract]);

  const handlePosterChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setPosterFile(file);
      const reader = new FileReader();
      reader.onloadend = () => setPosterPreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleTicketImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setTicketImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => setTicketImagePreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  // Fungsi untuk mengunggah file ke Pinata melalui backend
  const uploadToPinata = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:4000/upload-to-pinata', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      return res.data.pinataUrl;
    } catch (error) {
      console.error('Gagal mengunggah ke Pinata:', error);
      throw new Error('Upload ke Pinata gagal');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!contract || !userAddress) {
      alert('Pastikan kontrak dan dompet Anda terhubung.');
      return;
    }

    if (!eventName || !eventOrganizer || !eventDate || !ticketPrice || !saleEndDate || !posterFile || !ticketImageFile) {
      alert('Harap isi semua kolom.');
      return;
    }

    setLoading(true);
    try {
      // Unggah file ke Pinata dan dapatkan URL
      const posterUrl = await uploadToPinata(posterFile);
      const ticketImageUrl = await uploadToPinata(ticketImageFile);

      const priceInWei = ethers.utils.parseEther(ticketPrice);
      const saleEnd = Math.floor(new Date(saleEndDate).getTime() / 1000);
      const eventDateTimestamp = Math.floor(new Date(eventDate).getTime() / 1000);
      const barcode = Math.floor(Math.random() * 1e12); // Barcode acak

      const tx = await contract.createTicket(
        eventName,
        eventOrganizer,
        posterUrl,
        ticketImageUrl,
        priceInWei,
        saleEnd,
        eventDateTimestamp,
        barcode,
        { value: ethers.utils.parseEther('0.05') }
      );

      await tx.wait();
      alert('Tiket berhasil dibuat!');

      // Reset form
      setEventName('');
      setEventOrganizer('');
      setEventDate('');
      setTicketPrice('');
      setSaleEndDate('');
      setPosterFile(null);
      setTicketImageFile(null);
      setPosterPreview('');
      setTicketImagePreview('');
      setTicketCount(await contract.getTicketCount().toNumber());
    } catch (error) {
      console.error('Gagal membuat tiket:', error);
      alert('Gagal membuat tiket: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Penjualan Tiket Konser Musik Online</h2>
      <p>Jumlah Tiket: {ticketCount}</p>

      <label>
        Nama Acara:
        <input
          type="text"
          id="eventName"
          placeholder="Masukkan nama acara"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          required
        />
      </label>

      <label>
        Event Organizer:
        <input
          type="text"
          id="eventOrganizer"
          placeholder="Masukkan penyelenggara"
          value={eventOrganizer}
          onChange={(e) => setEventOrganizer(e.target.value)}
          required
        />
      </label>

      <label>
        Tanggal Acara:
        <input
          type="date" // Hanya tanggal
          id="eventDate"
          value={eventDate}
          onChange={(e) => setEventDate(e.target.value)}
          required
        />
      </label>

      <label>
        Tanggal Akhir Penjualan:
        <input
          type="date" // Hanya tanggal
          id="saleEndDate"
          value={saleEndDate}
          onChange={(e) => setSaleEndDate(e.target.value)}
          required
        />
      </label>

      <label>
        Harga Tiket (ETH):
        <input
          type="text"
          id="ticketPrice"
          placeholder="Masukkan harga tiket"
          value={ticketPrice}
          onChange={(e) => setTicketPrice(e.target.value)}
          required
        />
      </label>

      <label>
        Unggah Poster Tiket:
        <input
          type="file"
          accept="image/*"
          onChange={handlePosterChange}
          required
        />
        {posterPreview && <img src={posterPreview} alt="Preview poster" style={{ width: '200px' }} />}
      </label>

      <label>
        Unggah Gambar Tiket:
        <input
          type="file"
          accept="image/*"
          onChange={handleTicketImageChange}
          required
        />
        {ticketImagePreview && <img src={ticketImagePreview} alt="Preview gambar tiket" style={{ width: '200px' }} />}
      </label>

      <button type="submit" disabled={loading}>
        {loading ? 'Loading...' : 'Buat Tiket'}
      </button>
    </form>
  );
}

export default CreateSaleForm;
