import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';

function TicketList({ contract }) {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const ticketCount = await contract.getTicketCount();
        const ticketList = [];

        for (let i = 1; i <= ticketCount; i++) {
          const ticket = await contract.getTicket(i);
          ticketList.push(ticket);
        }

        setTickets(ticketList);
        console.log('Fetched tickets:', ticketList);
      } catch (error) {
        console.error('Error fetching tickets:', error);
      }
    };

    if (contract) {
      fetchTickets();
    }
  }, [contract]);

  const handleDownload = async (ticketId) => {
    try {
      await contract.markAsDownloaded(ticketId);
      alert(`Tiket ${ticketId} telah diunduh.`);
    } catch (error) {
      console.error('Error downloading ticket:', error);
      alert('Gagal mengunduh tiket.');
    }
  };

  return (
    <div>
      <h2>Daftar Tiket</h2>
      {tickets.length === 0 ? (
        <p>Tidak ada tiket yang tersedia.</p>
      ) : (
        <ul>
          {tickets.map((ticket, index) => (
            <li key={index}>
              <h3>{ticket.eventName}</h3>
              <p>Organiser: {ticket.eventOrganizer}</p>
              <img src={ticket.posterUrl} alt={ticket.eventName} style={{ width: '200px', height: 'auto' }} />
              <p>Tanggal Acara: {new Date(ticket.eventDate * 1000).toLocaleString()}</p>
              <p>Batas Penjualan: {new Date(ticket.expiryTimestamp * 1000).toLocaleString()}</p>
              <p>Harga: {ethers.utils.formatEther(ticket.price)} ETH</p>
              <button onClick={() => handleDownload(index + 1)} disabled={ticket.downloaded}>
                {ticket.downloaded ? 'Tiket Sudah Diunduh' : 'Unduh Tiket'}
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TicketList;
