import { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import axios from 'axios';

function CreateSaleForm({ contract, userAddress }) {
  const [eventName, setEventName] = useState('');
  const [eventOrganizer, setEventOrganizer] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [ticketPrice, setTicketPrice] = useState('');
  const [saleEndDate, setSaleEndDate] = useState('');
  const [posterFile, setPosterFile] = useState(null);
  const [ticketImageFile, setTicketImageFile] = useState(null);
  const [posterPreview, setPosterPreview] = useState('');
  const [ticketImagePreview, setTicketImagePreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [ticketCount, setTicketCount] = useState(0);
  
  const [posterCid, setPosterCid] = useState('');
  const [ticketImageCid, setTicketImageCid] = useState('');

  useEffect(() => {
    const fetchTicketCount = async () => {
      if (contract) {
        try {
          const count = await contract.getTicketCount();
          setTicketCount(count.toNumber());
        } catch (error) {
          console.error('Error loading ticket count:', error);
        }
      }
    };

    fetchTicketCount();
  }, [contract]);

  const handlePosterChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setPosterFile(file);
      const reader = new FileReader();
      reader.onloadend = () => setPosterPreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleTicketImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setTicketImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => setTicketImagePreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const uploadToPinata = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return res.data.cid;
    } catch (error) {
      console.error('Failed to upload to Pinata:', error.response?.data || error);
      throw new Error('Upload to Pinata failed');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    if (!contract || !userAddress) {
      alert('Ensure contract and wallet are connected.');
      return;
    }
  
    if (!eventName || !eventOrganizer || !eventDate || !ticketPrice || !saleEndDate || !posterFile || !ticketImageFile) {
      alert('Please fill in all fields.');
      return;
    }
  
    setLoading(true);
    try {
      const ticketImageUrl = await uploadToPinata(ticketImageFile);
      setTicketImageCid(ticketImageUrl);
      const posterUrl = await uploadToPinata(posterFile);
      setPosterCid(posterUrl);
  
      const priceInWei = ethers.utils.parseEther(ticketPrice);
      const saleEnd = Math.floor(new Date(saleEndDate).getTime() / 1000);
      const eventDateTimestamp = Math.floor(new Date(eventDate).getTime() / 1000);
      const barcode = Math.floor(Math.random() * 1e12);
  
      const tx = await contract.createTicket(
        eventName,
        eventOrganizer,
        posterCid,
        ticketImageCid,
        priceInWei,
        saleEnd,
        eventDateTimestamp,
        barcode,
        { value: ethers.utils.parseEther('0.05') }
      );
  
      await tx.wait();
      alert('Ticket successfully created!');

      // Reset form
      setEventName('');
      setEventOrganizer('');
      setEventDate('');
      setSaleEndDate('');
      setTicketPrice('');
      setPosterFile(null);
      setTicketImageFile(null);
      setPosterPreview('');
      setTicketImagePreview('');
      setPosterCid('');
      setTicketImageCid('');
      setTicketCount(prevCount => prevCount + 1); // Update ticket count
    } catch (error) {
      console.error('Failed to create ticket:', error);
      alert('Failed to create ticket: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Online Music Concert Ticket Sale</h2>
      <p>Ticket Count: {ticketCount}</p>

      <label>
        Event Name:
        <input
          type="text"
          id="eventName"
          placeholder="Enter event name"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          required
        />
      </label>

      <label>
        Event Organizer:
        <input
          type="text"
          id="eventOrganizer"
          placeholder="Enter organizer"
          value={eventOrganizer}
          onChange={(e) => setEventOrganizer(e.target.value)}
          required
        />
      </label>

      <label>
        Event Date:
        <input
          type="date"
          id="eventDate"
          value={eventDate}
          onChange={(e) => setEventDate(e.target.value)}
          required
        />
      </label>

      <label>
        Sale End Date:
        <input
          type="date"
          id="saleEndDate"
          value={saleEndDate}
          onChange={(e) => setSaleEndDate(e.target.value)}
          required
        />
      </label>

      <label>
        Ticket Price (ETH):
        <input
          type="number"
          id="ticket-price"
          name="ticketPrice"
          step="0.01"
          placeholder="Enter price (ETH)"
          value={ticketPrice}
          onChange={(e) => setTicketPrice(e.target.value)}
          required
        />
      </label>

      <label>
        Upload Ticket Poster:
        <input
          type="file"
          accept="image/*"
          onChange={handlePosterChange}
          required
        />
        {posterPreview && <img src={posterPreview} alt="Poster preview" style={{ width: '200px' }} />}
      </label>

      <label>
        Upload Ticket Image:
        <input
          type="file"
          accept="image/*"
          onChange={handleTicketImageChange}
          required
        />
        {ticketImagePreview && <img src={ticketImagePreview} alt="Ticket image preview" style={{ width: '200px' }} />}
      </label>

      <button type="submit" disabled={loading}>
        {loading ? 'Loading...' : 'Create Ticket'}
      </button>
    </form>
  );
}

export default CreateSaleForm;
