import React, { useState } from 'react';
import { ethers } from 'ethers';

function CreateSaleForm({ provider, contractAddress }) {
  const [eventName, setEventName] = useState('');
  const [eventOrganizer, setEventOrganizer] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [price, setPrice] = useState('');
  const [saleEndDate, setSaleEndDate] = useState('');
  const [poster, setPoster] = useState(null); // File untuk poster
  const [ticketImage, setTicketImage] = useState(null); // File untuk gambar tiket
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fungsi untuk menangani unggahan file
  const handleFileChange = (e, setFile) => {
    setFile(e.target.files[0]);
  };

  // Fungsi untuk mengonversi tanggal ke timestamp
  const toTimestamp = (date) => Math.floor(new Date(date).getTime() / 1000);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!provider) {
      alert('Harap hubungkan ke MetaMask terlebih dahulu.');
      return;
    }

    try {
      setIsSubmitting(true);

      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, TicketSale.abi, signer);

      const eventTimestamp = toTimestamp(eventDate);
      const saleEndTimestamp = toTimestamp(saleEndDate);

      if (saleEndTimestamp >= eventTimestamp) {
        alert('Tanggal akhir penjualan harus sebelum tanggal acara.');
        setIsSubmitting(false);
        return;
      }

      const ticketPriceInWei = ethers.utils.parseEther(price);

      // Memastikan poster dan tiket diunggah
      if (!poster || !ticketImage) {
        alert('Harap unggah poster dan gambar tiket.');
        setIsSubmitting(false);
        return;
      }

      const tx = await contract.createTicket(
        eventName,
        eventOrganizer,
        eventTimestamp,
        saleEndTimestamp,
        ticketPriceInWei,
        poster.name, // Anda bisa mengubah untuk menambahkan IPFS/URL di masa depan
        ticketImage.name, // Sama seperti di atas
        { value: ethers.utils.parseEther('0.01') } // Biaya pembuatan tiket
      );

      await tx.wait(); // Menunggu transaksi selesai
      alert('Tiket berhasil dibuat!');
    } catch (error) {
      console.error('Gagal membuat tiket:', error);
      alert('Terjadi kesalahan saat membuat tiket.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Nama Acara:</label>
        <input
          type="text"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Penyelenggara Acara:</label>
        <input
          type="text"
          value={eventOrganizer}
          onChange={(e) => setEventOrganizer(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Tanggal Acara:</label>
        <input
          type="date"
          value={eventDate}
          onChange={(e) => setEventDate(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Tanggal Akhir Penjualan:</label>
        <input
          type="date"
          value={saleEndDate}
          onChange={(e) => setSaleEndDate(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Harga Tiket (ETH):</label>
        <input
          type="number"
          step="0.01"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Unggah Poster:</label>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => handleFileChange(e, setPoster)}
          required
        />
      </div>

      <div>
        <label>Unggah Gambar Tiket:</label>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => handleFileChange(e, setTicketImage)}
          required
        />
      </div>

      <button type="submit" disabled={isSubmitting}>
        {isSubmitting ? 'Memproses...' : 'Buat Tiket'}
      </button>
    </form>
  );
}

export default CreateSaleForm;
