import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';

function TicketList({ contract }) {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  const getIpfsUrl = (cid) => `https://gateway.pinata.cloud/ipfs/${cid}`;

  useEffect(() => {
    const fetchTickets = async () => {
      setLoading(true);
      try {
        if (contract) {
          const ticketCount = await contract.getTicketCount();
          const ticketList = [];

          for (let i = 1; i <= ticketCount; i++) {
            const ticket = await contract.getTicket(i);
            ticketList.push(ticket);
          }

          setTickets(ticketList);
          console.log('Fetched tickets:', ticketList);
        }
      } catch (error) {
        console.error('Error fetching tickets:', error);
        alert('Failed to fetch tickets. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchTickets();
  }, [contract]);

  const handleDownload = async (ticketId) => {
    try {
      await contract.markAsDownloaded(ticketId);
      alert(`Ticket ${ticketId} has been downloaded.`);
    } catch (error) {
      console.error('Error downloading ticket:', error);
      alert('Failed to download ticket.');
    }
  };

  return (
    <div>
      <h2>Daftar Tiket</h2>
      {loading ? (
        <p>Loading tickets...</p>
      ) : tickets.length === 0 ? (
        <p>Belum ada tiket, silahkan buat penjualan.</p>
      ) : (
        tickets.map((ticket, index) => (
          <div key={index} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
            <h3>{ticket[0]}</h3> {/* eventName */}
            <img
              src={getIpfsUrl(ticket[2])} 
              alt={ticket[0]}
              style={{ width: '150px', height: 'auto', marginBottom: '10px' }}
            />
            <p>Organizer: {ticket[1]}</p> {/* eventOrganizer */}
            <p>Price: {ethers.utils.formatEther(ticket[3])} ETH</p> {/* price */}
            <p>Sale End: {new Date(ticket[4] * 1000).toLocaleString()}</p> {/* expiryTimestamp */}
            <button onClick={() => handleDownload(ticket[5])}>Download Ticket</button>
          </div>
        ))
      )}
    </div>
  );
}

export default TicketList;
