import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';

function TicketList({ contract }) {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fungsi untuk mengambil tiket dari kontrak
  const fetchTickets = async () => {
    setLoading(true);
    try {
        if (contract) {
            console.log("Contract is available. Fetching ticket count...");
            const ticketCount = await contract.getTicketCount();
            console.log(`Total tickets available: ${ticketCount.toString()}`);
            const ticketList = [];

            for (let i = 1; i <= ticketCount; i++) {
                console.log(`Fetching ticket with ID: ${i}`);
                const ticket = await contract.getTicket(i);

                // Tambahkan log untuk memeriksa nilai yang diterima
                console.log(`Ticket ${i} data:`, ticket);

                // Lakukan pengecekan untuk menghindari BigNumber error
                const price = ticket[3] ? ethers.BigNumber.from(ticket[3]) : ethers.BigNumber.from(0);
                
                ticketList.push({
                    id: i,
                    eventName: ticket[0],
                    eventOrganizer: ticket[1],
                    posterUrl: ticket[2] || "default_poster_url.png", // Atur default jika kosong
                    price: price,
                    expiryTimestamp: ticket[4].toNumber(), // Konversi BigNumber ke angka
                    sold: ticket[5],
                    downloaded: ticket[6],
                });
                console.log(`Ticket ${i}:`, ticket);
            }

            setTickets(ticketList);
            console.log('Fetched tickets:', ticketList);
        } else {
            console.error("Contract is not defined.");
            alert('Contract not defined. Please try again later.');
        }
    } catch (error) {
        console.error('Error fetching tickets:', error);
        alert('Failed to fetch tickets. Please try again later.');
    } finally {
        setLoading(false);
    }
};

  useEffect(() => {
    if (contract) {
      fetchTickets();
    } else {
      console.error("Contract is not available");
    }
  }, [contract]);

  const handleBuyTicket = async (ticketId, price) => {
    try {
      console.log(`Buying ticket ${ticketId} for price:`, ethers.utils.formatEther(price));
      const tx = await contract.buyTicket(ticketId, { value: price });
      await tx.wait();
      alert(`Ticket ${ticketId} successfully purchased.`);
      
      // Fetch updated tickets after purchase
      fetchTickets();
    } catch (error) {
      console.error('Error buying ticket:', error);
      alert('Failed to buy ticket.');
    }
  };

  const handleDownload = async (ticketId) => {
    try {
      console.log(`Downloading ticket ${ticketId}`);
      const tx = await contract.markAsDownloaded(ticketId);
      await tx.wait();
      alert(`Ticket ${ticketId} has been downloaded.`);
      
      // Fetch updated tickets after download
      fetchTickets();
    } catch (error) {
      console.error('Error downloading ticket:', error);
      alert('Failed to download ticket.');
    }
  };

  return (
    <div>
      <h2>Daftar Tiket</h2>
      {loading ? (
        <p>Loading tickets...</p>
      ) : tickets.length === 0 ? (
        <p>Belum ada tiket, silahkan buat penjualan.</p>
      ) : (
        tickets.map((ticket) => (
          <div key={ticket.id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
            <h3>{ticket.eventName}</h3>
            <img 
              src={ticket.posterUrl} 
              alt={ticket.eventName} 
              style={{ width: '150px', height: 'auto', marginBottom: '10px' }} 
            />
            <p>Organizer: {ticket.eventOrganizer}</p>
            <p>Price: {ethers.utils.formatEther(ticket.price)} ETH</p>
            <p>Sale End: {new Date(ticket.expiryTimestamp * 1000).toLocaleString()}</p>

            {ticket.sold ? (
              <button onClick={() => handleDownload(ticket.id)} disabled={ticket.downloaded}>
                {ticket.downloaded ? 'Ticket Already Downloaded' : 'Download Ticket'}
              </button>
            ) : (
              <button onClick={() => handleBuyTicket(ticket.id, ticket.price)}>
                Beli Tiket
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
}

export default TicketList;
