import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import CreateSaleForm from './components/CreateSaleForm';
import TicketList from './components/TicketList';
import './App.css';
import { contractAddress, contractABI } from './contractConfig';

const App = () => {
  const [account, setAccount] = useState('');
  const [contract, setContract] = useState(null);
  const [balance, setBalance] = useState(''); // State untuk menyimpan saldo

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);
        console.log("Connected account:", accounts[0]);

        // Mendapatkan saldo ETH pengguna
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const userBalance = await provider.getBalance(accounts[0]);
        setBalance(ethers.utils.formatEther(userBalance)); // Format saldo dari Wei ke ETH

        // Menghubungkan ke kontrak pintar
        const signer = provider.getSigner();
        const ticketContract = new ethers.Contract(contractAddress, contractABI, signer);
        setContract(ticketContract);
      } catch (error) {
        console.error("User denied wallet connection:", error);
      }
    } else {
      alert("Please install Metamask");
    }
  };

  useEffect(() => {
    connectWallet();
  }, []);

  return (
    <div className="container">
      <div className="user-info">
        {account ? (
          <>
            <p>Connected as: {account}</p>
            <p>Balance: {balance} ETH</p> {/* Tampilkan saldo ETH */}
          </>
        ) : (
          <p>Please connect your wallet.</p>
        )}
      </div>
      <div className="ticket-list-panel">
        {contract && <TicketList contract={contract} />}
      </div>
      <div className="form-panel">
        {contract && <CreateSaleForm contract={contract} userAddress={account} />}
      </div>
    </div>
  );
};

export default App;
