import { useState, useEffect } from 'react';
import { ethers } from 'ethers';

function CreateSaleForm({ contract, userAddress }) {
  const [eventName, setEventName] = useState('');
  const [eventOrganizer, setEventOrganizer] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [ticketPrice, setTicketPrice] = useState('');
  const [saleEndDate, setSaleEndDate] = useState('');
  const [posterUrl, setPosterUrl] = useState('');
  const [ticketImageUrl, setTicketImageUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [posterPreview, setPosterPreview] = useState('');
  const [ticketImagePreview, setTicketImagePreview] = useState('');
  const [ticketCount, setTicketCount] = useState(0);

  useEffect(() => {
    const fetchTicketCount = async () => {
      if (contract) {
        try {
          const count = await contract.getTicketCount();
          setTicketCount(count.toNumber());
        } catch (error) {
          console.error('Error memuat jumlah tiket:', error);
        }
      }
    };

    fetchTicketCount();
  }, [contract]);

  const handlePosterChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPosterUrl(reader.result);
        setPosterPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleTicketImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setTicketImageUrl(reader.result);
        setTicketImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!contract || !userAddress) {
      alert('Pastikan kontrak dan dompet Anda terhubung.');
      return;
    }

    if (
      !eventName ||
      !eventOrganizer ||
      !eventDate ||
      !ticketPrice ||
      !saleEndDate ||
      !posterUrl ||
      !ticketImageUrl
    ) {
      alert('Harap isi semua kolom.');
      return;
    }

    let priceInWei;
    try {
      priceInWei = ethers.utils.parseEther(ticketPrice);
    } catch (err) {
      alert('Format harga tiket tidak valid. Masukkan jumlah ETH yang benar.');
      return;
    }

    const saleEnd = Math.floor(new Date(saleEndDate).getTime() / 1000);
    const eventDateTimestamp = Math.floor(new Date(eventDate).getTime() / 1000);
    const barcode = Math.floor(Math.random() * 1e12); // Barcode acak

    if (priceInWei.isZero()) {
      alert('Harga tiket tidak boleh 0.');
      return;
    }

    if (saleEnd >= eventDateTimestamp) {
      alert('Batas penjualan harus sebelum tanggal acara.');
      return;
    }

    const now = Math.floor(Date.now() / 1000);
    if (eventDateTimestamp <= now) {
      alert('Tanggal acara harus lebih besar dari sekarang.');
      return;
    }

    setLoading(true);
    try {
      const tx = await contract.createTicket(
        eventName,
        eventOrganizer,
        posterUrl,
        ticketImageUrl,
        priceInWei,
        saleEnd,
        eventDateTimestamp,
        barcode, // Sertakan barcode di sini
        { value: ethers.utils.parseEther('0.05'), gasLimit: 3000000 } // Biaya untuk admin
      );

      await tx.wait();
      alert('Tiket berhasil dibuat!');

      // Reset form
      setEventName('');
      setEventOrganizer('');
      setEventDate('');
      setTicketPrice('');
      setSaleEndDate('');
      setPosterUrl('');
      setTicketImageUrl('');
      setPosterPreview('');
      setTicketImagePreview('');

      // Perbarui jumlah tiket
      const updatedCount = await contract.getTicketCount();
      setTicketCount(updatedCount.toNumber());
    } catch (error) {
      console.error('Error creating sale:', error);
      alert('Gagal membuat tiket: ' + (error.data?.message || error.message || 'Silakan coba lagi.'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Penjualan Tiket Konser Musik Online</h2>
      <p>Jumlah Tiket: {ticketCount}</p>

      <label>
        Nama Acara:
        <input
          type="text"
          id="eventName"
          placeholder="Masukkan nama acara"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          required
        />
      </label>

      <label>
        Event Organizer (EO):
        <input
          type="text"
          id="eventOrganizer"
          placeholder="Masukkan EO"
          value={eventOrganizer}
          onChange={(e) => setEventOrganizer(e.target.value)}
          required
        />
      </label>

      <label>
        Tanggal Acara:
        <input
          type="date"
          id="eventDate"
          value={eventDate}
          onChange={(e) => setEventDate(e.target.value)}
          required
        />
      </label>

      <label>
        Batas Tanggal Penjualan:
        <input
          type="datetime-local"
          id="saleEndDate"
          value={saleEndDate}
          onChange={(e) => setSaleEndDate(e.target.value)}
          required
        />
      </label>

      <label>
        Harga Tiket (ETH):
        <input
          type="number"
          step="0.01"
          id="ticketPrice"
          placeholder="Harga (ETH)"
          value={ticketPrice}
          onChange={(e) => setTicketPrice(e.target.value)}
          required
        />
      </label>

      <label>
        Poster Event:
        <input
          type="file"
          id="posterFile"
          accept="image/*"
          onChange={handlePosterChange}
          required
        />
      </label>

      {posterPreview && <img src={posterPreview} alt="Poster" style={{ width: '100%', maxWidth: '400px' }} />}

      <label>
        Gambar Tiket:
        <input
          type="file"
          id="ticketImageFile"
          accept="image/*"
          onChange={handleTicketImageChange}
          required
        />
      </label>

      {ticketImagePreview && <img src={ticketImagePreview} alt="Gambar Tiket" style={{ width: '100%', maxWidth: '400px' }} />}

      <button type="submit" disabled={loading}>
        {loading ? 'Membuat...' : 'Buat Tiket'}
      </button>
    </form>
  );
}

export default CreateSaleForm;
