import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers'; 

function TicketList({ contract }) {
  const [tickets, setTickets] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const loadTickets = async () => {
      if (!contract) {
        console.error("Contract is not defined.");
        setErrorMessage("Kontrak tidak terdefinisi.");
        return;
      }

      try {
        const ticketCount = await contract.getTicketCount();
        const ticketsArray = [];

        for (let i = 0; i < ticketCount; i++) {
          const ticket = await contract.tickets(i);
          ticketsArray.push(ticket);
        }

        setTickets(ticketsArray);
      } catch (error) {
        console.error('Error loading tickets:', error);
        setErrorMessage('Error memuat tiket.');
      }
    };

    loadTickets();
  }, [contract]);

  if (errorMessage) {
    return <p>{errorMessage}</p>;
  }

  return (
    <div>
      <h2>Daftar Tiket</h2>
      {tickets.length > 0 ? (
        tickets.map((ticket, index) => (
          <div key={index}>
            <p>Event: {ticket.eventName}</p>
            <p>Organizer: {ticket.eventOrganizer}</p>
            <p>Harga: {ethers.utils.formatEther(ticket.price)} ETH</p>
            <p>Tanggal Event: {new Date(ticket.eventDate * 1000).toLocaleString()}</p>
            <p>Barcode: {ticket.barcode}</p>
            <img src={ticket.posterUrl} alt="Poster" style={{ width: '100%', maxWidth: '200px' }} />
            <img src={ticket.ticketImageUrl} alt="Tiket" style={{ width: '100%', maxWidth: '200px' }} />
          </div>
        ))
      ) : (
        <p>Belum ada tiket yang dijual.</p>
      )}
    </div>
  );
}

export default TicketList;
