import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';

function TicketList({ contract }) {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const ticketCount = await contract.getTicketCount();
        const ticketList = [];

        for (let i = 1; i <= ticketCount; i++) { // Perhatikan bahwa kita mulai dari 1
          const ticket = await contract.getTicket(i); // Gunakan fungsi getter
          ticketList.push(ticket);
        }

        setTickets(ticketList);
        console.log('Fetched tickets:', ticketList);
      } catch (error) {
        console.error('Error fetching tickets:', error);
      }
    };

    if (contract) {
      fetchTickets();
    }
  }, [contract]);

  return (
    <div>
      <h2>Daftar Tiket</h2>
      {tickets.length === 0 ? (
        <p>Tidak ada tiket yang tersedia.</p>
      ) : (
        <ul>
          {tickets.map((ticket, index) => (
            <li key={index}>
              <h3>{ticket.eventName}</h3>
              <p>Organiser: {ticket.eventOrganizer}</p>
              <p>Harga: {ethers.utils.formatEther(ticket.price)} ETH</p> {/* Ubah dari wei ke ETH */}
              <p>Tanggal Acara: {new Date(ticket.eventDate * 1000).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TicketList;
