import React, { useState } from 'react';
import { ethers } from 'ethers';
import TicketSale from './abis/TicketSale.json'; // ABI kontrak harus benar

function CreateSaleForm({ provider, contractAddress }) {
  const [eventName, setEventName] = useState('');
  const [eventOrganizer, setEventOrganizer] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [price, setPrice] = useState('');
  const [saleEndDate, setSaleEndDate] = useState('');
  const [poster, setPoster] = useState(null);
  const [ticketImage, setTicketImage] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleFileChange = (e, setFile) => {
    setFile(e.target.files[0]);
  };

  const toTimestamp = (date) => Math.floor(new Date(date).getTime() / 1000);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!provider) {
      alert('Harap hubungkan ke MetaMask terlebih dahulu.');
      return;
    }

    try {
      setIsSubmitting(true);

      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, TicketSale.abi, signer);

      const eventTimestamp = toTimestamp(eventDate);
      const saleEndTimestamp = toTimestamp(saleEndDate);

      if (saleEndTimestamp >= eventTimestamp) {
        alert('Tanggal akhir penjualan harus sebelum tanggal acara.');
        setIsSubmitting(false);
        return;
      }

      const ticketPriceInWei = ethers.utils.parseEther(price);

      if (!poster || !ticketImage) {
        alert('Harap unggah poster dan gambar tiket.');
        setIsSubmitting(false);
        return;
      }

      const tx = await contract.createTicket(
        eventName,
        eventOrganizer,
        eventTimestamp,
        saleEndTimestamp,
        ticketPriceInWei,
        poster.name,
        ticketImage.name,
        { value: ethers.utils.parseEther('0.01') }
      );

      await tx.wait();
      alert('Tiket berhasil dibuat!');
    } catch (error) {
      console.error('Gagal membuat tiket:', error);
      alert('Terjadi kesalahan saat membuat tiket.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="eventName">Nama Acara:</label>
        <input
          type="text"
          id="eventName"
          name="eventName"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="eventOrganizer">Penyelenggara Acara:</label>
        <input
          type="text"
          id="eventOrganizer"
          name="eventOrganizer"
          value={eventOrganizer}
          onChange={(e) => setEventOrganizer(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="eventDate">Tanggal Acara:</label>
        <input
          type="date"
          id="eventDate"
          name="eventDate"
          value={eventDate}
          onChange={(e) => setEventDate(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="saleEndDate">Tanggal Akhir Penjualan:</label>
        <input
          type="date"
          id="saleEndDate"
          name="saleEndDate"
          value={saleEndDate}
          onChange={(e) => setSaleEndDate(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="price">Harga Tiket (ETH):</label>
        <input
          type="number"
          step="0.01"
          id="price"
          name="price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="poster">Unggah Poster:</label>
        <input
          type="file"
          id="poster"
          name="poster"
          accept="image/*"
          onChange={(e) => handleFileChange(e, setPoster)}
          required
        />
      </div>

      <div>
        <label htmlFor="ticketImage">Unggah Gambar Tiket:</label>
        <input
          type="file"
          id="ticketImage"
          name="ticketImage"
          accept="image/*"
          onChange={(e) => handleFileChange(e, setTicketImage)}
          required
        />
      </div>

      <button type="submit" disabled={isSubmitting}>
        {isSubmitting ? 'Memproses...' : 'Buat Tiket'}
      </button>
    </form>
  );
}

export default CreateSaleForm;
